package Class_Materials;

public class IF_Else_And {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a, b,c;
		a=4000;
		b=40;
		c=500;
		
		if(a>b && a>c)
		{
		System.out.println("A >B and C");	
		}
		else if(b>a && b>c )
		{
			System.out.println("B>A and C");	
	
			
		}
		else 
		{
			System.out.println("C > A and B");	
		}
		
		
	}

}
